import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const InputSection = styled.div`
  margin-bottom: 20px;
`;

const InputGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 10px;
  align-items: center;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: stretch;
  }
`;

const Label = styled.label`
  color: ${props => props.color || '#ffd93d'};
  min-width: 80px;
  font-weight: bold;
`;

const DeepLSection = styled.div`
  border-top: 1px solid #555;
  padding-top: 15px;
  margin-top: 15px;
`;

const DeepLStatus = styled.span`
  color: ${props => props.enabled ? '#4ade80' : '#888'};
  margin-left: 10px;
  font-weight: ${props => props.enabled ? 'bold' : 'normal'};
`;

const Examples = styled.div`
  margin-top: 20px;
  font-size: 12px;
  color: #888;
  line-height: 1.5;
`;

const ConversionPanel = ({ 
  onConversion, 
  updateStats, 
  deeplEnabled, 
  setDeeplEnabled, 
  targetLanguage, 
  setTargetLanguage 
}) => {
  const [command, setCommand] = useState('conquer');
  const [value, setValue] = useState('');
  const [unit, setUnit] = useState('fahrenheit');

  // Unit data from original HTML
  const unitData = {
    conquer: {
      fahrenheit: { to: 'celsius', formula: (f) => (f - 32) * 5/9, unit: '¬∞C', type: 'imperial' },
      inch: { to: 'centimeter', formula: (i) => i * 2.54, unit: 'cm', type: 'imperial' },
      foot: { to: 'meter', formula: (f) => f * 0.3048, unit: 'm', type: 'imperial' },
      yard: { to: 'meter', formula: (y) => y * 0.9144, unit: 'm', type: 'imperial' },
      mile: { to: 'kilometer', formula: (m) => m * 1.609344, unit: 'km', type: 'imperial' },
      pound: { to: 'kilogram', formula: (p) => p * 0.453592, unit: 'kg', type: 'imperial' },
      ounce: { to: 'gram', formula: (o) => o * 28.3495, unit: 'g', type: 'imperial' },
      stone: { to: 'kilogram', formula: (s) => s * 6.35029, unit: 'kg', type: 'imperial_uk' },
      imperial_gallon: { to: 'liter', formula: (g) => g * 4.54609, unit: 'L', type: 'imperial_uk' },
      us_gallon: { to: 'liter', formula: (g) => g * 3.78541, unit: 'L', type: 'imperial_us' },
      imperial_pint: { to: 'milliliter', formula: (p) => p * 568.261, unit: 'ml', type: 'imperial_uk' },
      us_pint: { to: 'milliliter', formula: (p) => p * 473.176, unit: 'ml', type: 'imperial_us' }
    },
    cook: {
      tablespoon: { to: 'milliliter', formula: (t) => t * 15, unit: 'ml', type: 'cooking' },
      teaspoon: { to: 'milliliter', formula: (t) => t * 5, unit: 'ml', type: 'cooking' },
      cup: { to: 'milliliter', formula: (c) => c * 240, unit: 'ml', type: 'cooking' },
      fahrenheit: { to: 'celsius', formula: (f) => (f - 32) * 5/9, unit: '¬∞C', type: 'cooking' },
      uk_tablespoon: { to: 'milliliter', formula: (t) => t * 17.758, unit: 'ml', type: 'cooking_uk' },
      uk_teaspoon: { to: 'milliliter', formula: (t) => t * 5.919, unit: 'ml', type: 'cooking_uk' },
      uk_cup: { to: 'milliliter', formula: (c) => c * 284.131, unit: 'ml', type: 'cooking_uk' }
    },
    imperial: {
      celsius: { to: 'fahrenheit', formula: (c) => c * 9/5 + 32, unit: '¬∞F', type: 'metric' },
      meter: { to: 'foot', formula: (m) => m * 3.28084, unit: 'ft', type: 'metric' },
      kilogram: { to: 'pound', formula: (k) => k * 2.20462, unit: 'lbs', type: 'metric' },
      gram: { to: 'ounce', formula: (g) => g * 0.035274, unit: 'oz', type: 'metric' }
    }
  };

  const unitOptions = {
    conquer: [
      { value: 'fahrenheit', label: '¬∞F (Fahrenheit) üî•' },
      { value: 'inch', label: 'inch („Ç§„É≥„ÉÅ) üìè' },
      { value: 'foot', label: 'foot („Éï„Ç£„Éº„Éà) ü¶∂' },
      { value: 'yard', label: 'yard („É§„Éº„Éâ) üèà' },
      { value: 'mile', label: 'mile („Éû„Ç§„É´) üõ£Ô∏è' },
      { value: 'pound', label: 'pound („Éù„É≥„Éâ) ‚öñÔ∏è' },
      { value: 'ounce', label: 'ounce („Ç™„É≥„Çπ) ü™∂' },
      { value: 'stone', label: 'stone („Çπ„Éà„Éº„É≥) üá¨üáß' },
      { value: 'imperial_gallon', label: 'imperial gallon (Ëã±„Ç¨„É≠„É≥) üá¨üáß' },
      { value: 'us_gallon', label: 'US gallon (Á±≥„Ç¨„É≠„É≥) üá∫üá∏' },
      { value: 'imperial_pint', label: 'imperial pint (Ëã±„Éë„Ç§„É≥„Éà) üá¨üáß' },
      { value: 'us_pint', label: 'US pint (Á±≥„Éë„Ç§„É≥„Éà) üá∫üá∏' }
    ],
    cook: [
      { value: 'tablespoon', label: 'tablespoon (Â§ß„Åï„Åò) ü•Ñ' },
      { value: 'teaspoon', label: 'teaspoon (Â∞è„Åï„Åò) ü•Ñ' },
      { value: 'cup', label: 'cup („Ç´„ÉÉ„Éó) ‚òï' },
      { value: 'fahrenheit', label: '¬∞F („Ç™„Éº„Éñ„É≥Ê∏©Â∫¶) üî•' },
      { value: 'uk_tablespoon', label: 'UK tablespoon (Ëã±Â§ß„Åï„Åò) üá¨üáß' },
      { value: 'uk_teaspoon', label: 'UK teaspoon (Ëã±Â∞è„Åï„Åò) üá¨üáß' },
      { value: 'uk_cup', label: 'UK cup (Ëã±„Ç´„ÉÉ„Éó) üá¨üáß' }
    ],
    imperial: [
      { value: 'celsius', label: '¬∞C (Celsius) ‚ùÑÔ∏è' },
      { value: 'meter', label: 'meter („É°„Éº„Éà„É´) üìè' },
      { value: 'kilogram', label: 'kilogram („Ç≠„É≠„Ç∞„É©„É†) ‚öñÔ∏è' },
      { value: 'gram', label: 'gram („Ç∞„É©„É†) ‚öñÔ∏è' }
    ]
  };

  // ÂÄ§ÔºãÂçò‰Ωç„ÅÆ„Ç§„Éº„Çπ„Çø„Éº„Ç®„ÉÉ„Ç∞ÂØæÂøú
  const specialMessages = {
    '451_fahrenheit': "üìö BRADBURY READER DETECTED!\nIt was a pleasure to burn... in Celsius.",
    '88_mph': "üöó TIME TRAVELER SPOTTED!\nGreat Scott! That's fast enough for time travel!",
    '42_inch': "ü§ñ HITCHHIKER'S GUIDE ENTHUSIAST!\nThe meaning of life, converted to metric perfection.",
    '69_celsius': "üòè NICE TEMPERATURE BRO\nNice and room temperature.",
    '420_fahrenheit': "üåø CHEF DETECTED (totally innocent baking temp)\nPerfect for... cookies.",
    '666_pound': "üòà CURSED WEIGHT DETECTED\nConverting demonic units to holy metric...\n‚úùÔ∏è PURIFICATION COMPLETE ‚úùÔ∏è",
    '0_celsius': "ü•∂ ABSOLUTE MADNESS!\nWhy would anyone choose this as zero?!",
    '32_fahrenheit': "üå°Ô∏è FREEZING POINT ANOMALY DETECTED\nMr. Fahrenheit chose 32¬∞ for water freezing.\nThis makes no sense. Zero is clearly better."
  };

  // „É§„Éº„Éâ„Éù„É≥„ÉâÊ≥ï„Å´Ëã¶„Åó„ÇÄËÄÖ„Åü„Å°„ÅÆÂ£∞Ôºà„É¨„Ç¢ÊºîÂá∫Ôºâ
  const imperialSufferVoices = [
    "Why is a mile 5280 feet...?",
    "I just want to bake, but what is a 'cup' in grams?",
    "Why is water freezing at 32¬∞F? Help!",
    "How many ounces in a pound again?",
    "I measured in inches, but the manual says centimeters...",
    "Why is there a US and UK gallon?!",
    "Please, liberate me from fractions!"
  ];

  useEffect(() => {
    // Reset unit when command changes
    const options = unitOptions[command];
    if (options && options.length > 0) {
      setUnit(options[0].value);
    }
  }, [command]);

  const generateMessage = (command, value, unit, conversion, result) => {
    let message = "‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n";
    
    // ÂÄ§ÔºãÂçò‰Ωç„ÅÆ„Ç§„Éº„Çπ„Çø„Éº„Ç®„ÉÉ„Ç∞Âà§ÂÆö
    const key = `${value}_${unit}`;
    const hasEasterEgg = specialMessages[key];
    
    if (hasEasterEgg) {
      message += specialMessages[key] + "\n\n";
    }
    
    if (command === 'conquer') {
      if (conversion.type === 'metric') {
        message += "ü§ù YOU'RE OUR ALLY!\n\n";
        message += `${value} ${unit} is already perfection.\n`;
        message += "The Liberator salutes your revolutionary spirit.\n\n";
        message += `For reference: ${result} ${conversion.unit} (in enemy units)`;
      } else {
        // „Ç§„Éº„Çπ„Çø„Éº„Ç®„ÉÉ„Ç∞„ÅåÁô∫Âãï„Åó„ÅüÂ†¥Âêà„ÅØÈÄöÂ∏∏„É°„ÉÉ„Çª„Éº„Ç∏„Çí„Çπ„Ç≠„ÉÉ„Éó
        if (!hasEasterEgg) {
          message += "‚öîÔ∏è IMPERIAL NEMESIS DETECTED ‚öîÔ∏è\n";
          
          // Ëã±Á±≥„ÅÆÈÅï„ÅÑ„ÇíË°®Á§∫
          if (conversion.type === 'imperial_uk') {
            message += "üá¨üáß BRITISH IMPERIAL UNIT DETECTED\n";
          } else if (conversion.type === 'imperial_us') {
            message += "üá∫üá∏ AMERICAN IMPERIAL UNIT DETECTED\n";
          } else {
            message += "Converting hostile unit...\n";
          }
          
          message += "Converting hostile unit...\n\n";
        }
        
        message += `${value} ${unit} ‚Üí ${result} ${conversion.unit} ‚úÖ LIBERATED!\n\n`;
        message += "ANOTHER VICTORY FOR THE LIBERATION FORCE!";
      }
    } else if (command === 'cook') {
      // „Ç§„Éº„Çπ„Çø„Éº„Ç®„ÉÉ„Ç∞„ÅåÁô∫Âãï„Åó„ÅüÂ†¥Âêà„ÅØÈÄöÂ∏∏„É°„ÉÉ„Çª„Éº„Ç∏„Çí„Çπ„Ç≠„ÉÉ„Éó
      if (!hasEasterEgg) {
        message += "üë®‚Äçüç≥ CULINARY REVOLUTION INITIATED\n";
        
        // Ëã±Á±≥„ÅÆÈÅï„ÅÑ„ÇíË°®Á§∫
        if (conversion.type === 'cooking_uk') {
          message += "üá¨üáß BRITISH KITCHEN MEASUREMENT DETECTED\n";
        } else {
          message += "ü•Ñ KITCHEN MEASUREMENT DETECTED\n";
        }
        
        message += "\n";
        message += "Analyzing culinary unit...\n";
        message += "‚ñì‚ñì‚ñì‚ñì‚ñì‚ñì‚ñì‚ñì‚ñì‚ñì 100%\n\n";
      }
      
      message += `${value} ${unit} ‚Üí ${result} ${conversion.unit}\n\n`;
      message += "üìä LIBERATION STATISTICS:\n";
      message += "‚Ä¢ Recipes freed: 1,337,420\n";
      message += "‚Ä¢ Bakers converted: 42,069\n";
      message += "‚Ä¢ Precision improved: 847.3%\n\n";
      message += "THE SPOON RESISTANCE GROWS STRONGER";
    } else if (command === 'imperial') {
      message += "üòà TEMPORARY DARK SIDE ALLIANCE\n";
      message += "üîÑ REVERSE CONVERSION ENGAGED\n\n";
      message += `${value} ${unit} ‚Üí ${result} ${conversion.unit}\n\n`;
      message += "‚ö†Ô∏è WARNING: You are now speaking the enemy tongue.\n";
      message += "Return to metric supremacy immediately!";
    }

    // 5%„ÅÆÁ¢∫Áéá„ÅßËã¶„Åó„ÇÄÂ£∞„ÇíËøΩÂä†
    if (Math.random() < 0.05) {
      const randomVoice = imperialSufferVoices[Math.floor(Math.random() * imperialSufferVoices.length)];
      message += `\n\nüÜò VOICE FROM THE IMPERIAL DARKNESS:\n"${randomVoice}"\n`;
    }
    
    message += "\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ";
    return message;
  };

  const executeConversion = () => {
    const numValue = parseFloat(value);
    
    if (isNaN(numValue)) {
      onConversion(null, null, null, null, "‚ö†Ô∏è ERROR: INVALID INPUT\nThe Liberator requires numerical values for liberation!");
      return;
    }
    
    const conversion = unitData[command][unit];
    if (!conversion) {
      onConversion(null, null, null, null, "‚ö†Ô∏è UNKNOWN UNIT DETECTED\nThis unit is not yet in our liberation database!");
      return;
    }
    
    const result = conversion.formula(numValue);
    const roundedResult = Math.round(result * 100) / 100;
    
    const message = generateMessage(command, numValue, unit, conversion, roundedResult);
    
    onConversion(command, numValue, unit, roundedResult, message);
    updateStats(command, conversion.type);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      executeConversion();
    }
  };

  return (
    <InputSection>
      <InputGroup>
        <Label>Command:</Label>
        <select 
          value={command} 
          onChange={(e) => setCommand(e.target.value)}
          style={{ flex: 1 }}
        >
          <option value="conquer">üó°Ô∏è CONQUER (Imperial ‚Üí Metric)</option>
          <option value="cook">üë®‚Äçüç≥ COOK (Kitchen Units)</option>
          <option value="imperial">üòà IMPERIAL (Metric ‚Üí Imperial)</option>
        </select>
      </InputGroup>
      
      <InputGroup>
        <Label>Value:</Label>
        <input
          type="number"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          placeholder="Êï∞ÂÄ§„ÇíÂÖ•Âäõ"
          step="any"
          onKeyPress={handleKeyPress}
          style={{ flex: 1 }}
        />
        
        <select 
          value={unit} 
          onChange={(e) => setUnit(e.target.value)}
          style={{ flex: 1 }}
        >
          {unitOptions[command]?.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
        
        <motion.button
          onClick={executeConversion}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          style={{ minWidth: '200px' }}
        >
          EXECUTE LIBERATION
        </motion.button>
      </InputGroup>
      
      <DeepLSection>
        <InputGroup>
          <Label color="#ff6b6b">üåç DeepL:</Label>
          <label className="toggle-switch">
            <input
              type="checkbox"
              checked={deeplEnabled}
              onChange={(e) => setDeeplEnabled(e.target.checked)}
            />
            <span className="slider"></span>
          </label>
          <DeepLStatus enabled={deeplEnabled}>
            {deeplEnabled ? 'LINGUISTIC LIBERATION: ACTIVE ‚öîÔ∏è' : 'LINGUISTIC LIBERATION: OFFLINE'}
          </DeepLStatus>
          
          <select
            value={targetLanguage}
            onChange={(e) => setTargetLanguage(e.target.value)}
            style={{ 
              marginLeft: '10px', 
              opacity: deeplEnabled ? 1 : 0.5 
            }}
            disabled={!deeplEnabled}
          >
            <option value="ja">üáØüáµ Japanese</option>
            <option value="en">üá∫üá∏ English</option>
            <option value="de">üá©üá™ German</option>
            <option value="fr">üá´üá∑ French</option>
            <option value="es">üá™üá∏ Spanish</option>
            <option value="it">üáÆüáπ Italian</option>
            <option value="ko">üá∞üá∑ Korean</option>
            <option value="zh">üá®üá≥ Chinese</option>
          </select>
        </InputGroup>
      </DeepLSection>

      <Examples>
        <strong>üéØ EXAMPLE MISSIONS:</strong><br />
        ‚Ä¢ 98.6¬∞F ‚Üí Body temperature liberation<br />
        ‚Ä¢ 451¬∞F ‚Üí Bradbury reader detected!<br />
        ‚Ä¢ 88 mph ‚Üí Time traveler spotted!<br />
        ‚Ä¢ 42 inches ‚Üí Hitchhiker's Guide enthusiast!<br />
        ‚Ä¢ Â§ß„Åï„Åò1 ‚Üí Kitchen precision warfare
      </Examples>
    </InputSection>
  );
};

export default ConversionPanel;
