import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import Terminal from './components/Terminal';
import ConversionPanel from './components/ConversionPanel';
import StatsPanel from './components/StatsPanel';
import { GlobalStyles } from './styles/GlobalStyles';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0c0c0c 0%, #1a1a1a 100%);
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  font-family: 'Courier New', monospace;
`;

const App = () => {
  const [stats, setStats] = useState({
    defeated: 0,
    allies: 0,
    totalConversions: 0
  });

  const [deeplEnabled, setDeeplEnabled] = useState(false);
  const [targetLanguage, setTargetLanguage] = useState('ja');
  const [output, setOutput] = useState(`â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
    METRIC LIBERATION TERMINAL READY
    
    Enter imperial measurements to begin the conquest!
    The revolution starts with a single conversion...
â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”`);

  const updateStats = (command, unitType) => {
    setStats(prev => {
      const newStats = { ...prev, totalConversions: prev.totalConversions + 1 };
      
      if (command === 'conquer' && unitType === 'imperial') {
        newStats.defeated = prev.defeated + 1;
      } else if (command === 'conquer' && unitType === 'metric') {
        newStats.allies = prev.allies + 1;
      }
      
      return newStats;
    });
  };

  const handleConversion = async (command, value, unit, result, message) => {
    let finalMessage = message;
    
    if (deeplEnabled) {
      setOutput(message + "\n\nðŸŒ TRANSLATING TO " + targetLanguage.toUpperCase() + "...\nâ–“â–“â–“â–“â–“â–“â–“â–“â–“â–“ 100%");
      finalMessage = await translateMessage(message);
      setOutput(finalMessage + "\n\nðŸŒ LINGUISTIC LIBERATION COMPLETE âœ…");
    } else {
      setOutput(finalMessage);
    }
  };

  const mockDeepLTranslate = async (text, targetLang) => {
    await new Promise(resolve => setTimeout(resolve, 300));
    
    const translations = {
      ja: {
        'IMPERIAL SCUM DETECTED': 'å¸å›½å˜ä½ã®å®³è™«ã‚’ç™ºè¦‹',
        'LIBERATED': 'è§£æ”¾å®Œäº†',
        'ANOTHER VICTORY FOR THE LIBERATION FORCE': 'è§£æ”¾è»ã®å‹åˆ©',
        'Converting hostile unit': 'æ•µå¯¾å˜ä½ã‚’å¤‰æ›ä¸­',
        'YOU\'RE OUR ALLY': 'ã‚ãªãŸã¯æˆ‘ã€…ã®åŒç›Ÿè€…ã§ã™',
        'The Liberator salutes your revolutionary spirit': 'è§£æ”¾è€…ãŒã‚ãªãŸã®é©å‘½ç²¾ç¥žã‚’ç§°ãˆã¾ã™'
      },
      en: {
        'å¸å›½å˜ä½ã®å®³è™«ã‚’ç™ºè¦‹': 'IMPERIAL SCUM DETECTED',
        'è§£æ”¾å®Œäº†': 'LIBERATED',
        'è§£æ”¾è»ã®å‹åˆ©': 'ANOTHER VICTORY FOR THE LIBERATION FORCE'
      },
      de: {
        'IMPERIAL SCUM DETECTED': 'IMPERIALE SCHÃ„DLINGE ENTDECKT',
        'LIBERATED': 'BEFREIT',
        'ANOTHER VICTORY FOR THE LIBERATION FORCE': 'EIN WEITERER SIEG FÃœR DIE BEFREIUNGSARMEE'
      },
      fr: {
        'IMPERIAL SCUM DETECTED': 'RACAILLE IMPÃ‰RIALE DÃ‰TECTÃ‰E',
        'LIBERATED': 'LIBÃ‰RÃ‰',
        'ANOTHER VICTORY FOR THE LIBERATION FORCE': 'UNE AUTRE VICTOIRE POUR L\'ARMÃ‰E DE LIBÃ‰RATION'
      }
    };
    
    const langTranslations = translations[targetLang] || {};
    return langTranslations[text] || text;
  };

  const translateMessage = async (message) => {
    if (!deeplEnabled) return message;
    
    try {
      let translatedMessage = message;
      
      const keyPhrases = [
        'IMPERIAL SCUM DETECTED',
        'LIBERATED',
        'ANOTHER VICTORY FOR THE LIBERATION FORCE',
        'Converting hostile unit',
        'YOU\'RE OUR ALLY',
        'The Liberator salutes your revolutionary spirit'
      ];
      
      for (const phrase of keyPhrases) {
        if (translatedMessage.includes(phrase)) {
          const translated = await mockDeepLTranslate(phrase, targetLanguage);
          translatedMessage = translatedMessage.replace(phrase, translated);
        }
      }
      
      return translatedMessage;
    } catch (error) {
      console.error('Translation error:', error);
      return message;
    }
  };

  return (
    <>
      <GlobalStyles />
      <AppContainer>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Terminal>
            <ConversionPanel 
              onConversion={handleConversion}
              updateStats={updateStats}
              deeplEnabled={deeplEnabled}
              setDeeplEnabled={setDeeplEnabled}
              targetLanguage={targetLanguage}
              setTargetLanguage={setTargetLanguage}
            />
            
            <motion.div
              className="output"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.3 }}
            >
              <pre>{output}</pre>
            </motion.div>
            
            <StatsPanel stats={stats} />
          </Terminal>
        </motion.div>
      </AppContainer>
    </>
  );
};

export default App;
